class Solution:
    mod = 1000000007

    def threeSumMulti(self, arr, target: int):
        if not arr or len(arr) < 3:
            return 0
        arr.sort()
        num = 0
        for i in range(len(arr)):
            t = target - arr[i]
            j = i + 1
            k = len(arr) - 1
            while j < k:
                if  arr[j] + arr[k] < t:
                    j += 1
                elif arr[j] + arr[k] > t:
                    k -= 1
                else:
                    if arr[j] == arr[k]:
                        num += ((k - j + 1) * (k - j)) // 2
                        num %= Solution.mod
                        break
                    else:
                        left = 1
                        while j + 1 < k and arr[j] == arr[j + 1]:
                            left += 1
                            j += 1
                        right = 1
                        while k - 1 > j and arr[k] == arr[k - 1]:
                            right += 1
                            k -= 1
                        num += left * right
                        num %= Solution.mod
                        j += 1
                        k -= 1
        return num






